import java.util.*

class NotQuiteLisp(val str: String) {
    var idx = -1
    val floor = str.foldIndexed(Stack<Char>()) {
        i, stack, c -> when (c) {
            '(' -> stack.push(c)
            ')' -> if (stack.size != 0 && stack.peek() == '(') stack.pop()
                   else {
                       stack.push(c)
                       if (idx == -1) idx = i + 1
                   }
        }
        stack
    }.sumBy { if (it == '(') 1 else -1 }
}

